const convertBtn=document.getElementById("convert-btn"),htmlInput=document.getElementById("html-input"),xmlOutput=document.getElementById("xml-output"),copyXmlBtn=document.getElementById("copy-xml-btn"),copySuccessMsg=document.getElementById("copy-success-msg");convertBtn.addEventListener("click",()=>{const e=htmlInput.value,t=escapeXml(e);xmlOutput.textContent=t,copyXmlBtn.style.display="block",copySuccessMsg.style.display="none"}),copyXmlBtn.addEventListener("click",()=>{const e=xmlOutput.textContent;copyToClipboard(e),copySuccessMsg.style.display="block"});function escapeXml(e){return e.replace(/[<>&'"]/g,function(e){switch(e){case"<":return"&lt;";case">":return"&gt;";case"&":return"&amp;";case"'":return"&apos;";case'"':return"&quot;"}})}function copyToClipboard(e){const t=document.createElement("textarea");t.value=e,document.body.appendChild(t),t.select(),document.execCommand("copy"),document.body.removeChild(t)}
