const imageInput=document.getElementById("image-input"),convertBtn=document.getElementById("convert-btn"),outputImage=document.getElementById("output-image"),downloadLink=document.getElementById("download-link"),uploadLabel=document.getElementById("upload-label"),fileInfo=document.getElementById("file-info"),resetBtn=document.getElementById("reset-btn");imageInput.addEventListener("change",()=>{const t=imageInput.files[0];t?(fileInfo.textContent=`File: ${t.name}, Size: ${(t.size/1024).toFixed(2)} KB`,uploadLabel.textContent="Image selected",convertBtn.style.display="block"): (uploadLabel.textContent="Choose an image",fileInfo.textContent="",convertBtn.style.display="none"),outputImage.style.display="none",downloadLink.style.display="none"}),convertBtn.addEventListener("click",()=>{const t=imageInput.files[0];if(t){const e=new FileReader;e.onload=function(e){try{const n=e.target.result,r=new Image;r.src=n,r.onload=function(){const e=document.createElement("canvas");e.width=r.width,e.height=r.height;const o=e.getContext("2d");o.drawImage(r,0,0),e.toBlob(function(e){const n=URL.createObjectURL(e);outputImage.src=n,outputImage.style.display="block",downloadLink.href=n,downloadLink.style.display="block"},"image/webp")};}catch(e){console.error("Error converting image:",e.message)}};e.readAsDataURL(t)}}),resetBtn.addEventListener("click",()=>{imageInput.value=null,uploadLabel.textContent="Choose an image",fileInfo.textContent="",convertBtn.style.display="none",outputImage.style.display="none",downloadLink.style.display="none"});
