const t=document.getElementById("text-input"),e=document.getElementById("word-count"),n=document.getElementById("char-count"),r=document.getElementById("letter-count"),o=document.getElementById("sentence-count"),c=document.getElementById("paragraph-count");t.addEventListener("input",()=>{const t=t.value,e=t.trim().split(/\s+/).length,n=t.length,r=t.replace(/[^a-zA-Z]/g,"").length,o=(t.match(/[.!?]+/g)||[]).length,c=t.trim().split(/\n+/).length;e.textContent=e,n.textContent=n,r.textContent=r,o.textContent=o,c.textContent=c});
